sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {

	initialize : function(){
		gs.addInfoMessage('Initializing NeedItUtils Script Include');
	},
	isDatePassed : function(strDate){
		var currentDate = new GlideDateTime();
		var testDate = new GlideDateTime(strDate);
		if(testDate.compareTo(currentDate) <= 0){
			return true;
		}else{
			return false;
		}
	},
    type: 'NeedItUtils'
};
